package com.productservice.config.integrations;import feign.Logger;import feign.RequestInterceptor;import feign.codec.ErrorDecoder;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @philosophy It's not "CODE" It's how you think... Think and solve the problem * Growth is when you keep finding efficient ways to solve the problem * <p> * ------ * Tip: Always code as if the guy who ends up maintaining your code will be a violent psychopath who knows where you live. * 'Cause he is violent,and he knows where you live. * ------ * Anyway, welcome to the InfoBipConfig class... HAPPY THINKING * @since 15/04/2022 4:18 PM */@Configurationpublic class InfoBipFeignConfiguration {    @Value("${integrations.info-bip.api-key:0987654321}")    private String apiKey;    @Bean    public RequestInterceptor requestInterceptor() {        return template -> template.header("Authorization", apiKey);    }    @Bean    Logger.Level feignLoggerLevel() {        return Logger.Level.FULL;    }    @Bean    public ErrorDecoder errorDecoder() {        return new StashErrorDecoder();    }}